// BookingOrder-list
const getAllBookingOrder = async (
  pageIndex: any = 1,
  pageSize: any = 10000,
  filters: any = {}
) => {
  try {
    let queryParams = `PageIndex=${pageIndex}&PageSize=10000`;

    if (filters.orderNo) {
      queryParams += `&OrderNo=${filters.orderNo}`;
    }

    const response = await apiFetch(
      `BookingOrder?${queryParams}`,
      { method: 'GET', headers: {} },
      true
    );

    
    if (response?.data) {
      response.data = response.data.sort(
        (a: any, b: any) => a.orderNo - b.orderNo
      );
    }

    return response;
  } catch (error: any) {
    throw error;
  }
};


